import React from "react";
import {
  Row,
  Col,
  Form,
  Button,
  Card,
  Table,
  Modal,
  Container,
} from "react-bootstrap";
import axios from "axios";
import NavBar from "./NavBar";

class ListOfReservations extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bookings: [],
    };
  }
  componentDidMount() {
    axios.get("http://localhost:8080/booking/").then((res) => {
      this.setState({
        bookings: res.data.map((booking) => {
          return {
            ...booking,
            // available: room.available === true ? "Yes" : "No",
          };
        }),
      }).catch((error) => console.log(error));
    });
  }
  render() {
    return (
      <div>
        <NavBar />
        <Container>
          <Row>
            <Col>
              <h3 style={{ textAlign: "center", color: "white" }}>
                List of Reservations
              </h3>
              <Table striped bordered hover size="sm">
                <thead style={{ color: "white" }}>
                  <tr>
                    <th>Arrival Date</th>
                    <th>Departure Date</th>
                    <th>Room Price</th>
                    <th>Number of Rooms</th>
                    <th>Other Services</th>
                    <th>Reserved By?</th>
                    <th colSpan={2} style={{ textAlign: "center" }}>
                      Action
                    </th>
                  </tr>
                </thead>
                <tbody style={{ color: "white" }}>
                  {this.state.bookings.map((booking) => (
                    <tr key={booking.bookingId}>
                      <td>{booking.dateOfArrival}</td>
                      <td>{booking.dateOfDeparture}</td>
                      <td>{booking.numberOfRooms}</td>
                      <td>{booking.otherReservations}</td>
                      {/* <td>{booking.description}</td>
                      <td>{booking.available}</td> */}
                      <td>
                        <Button
                          class="btn btn-primary"
                          onClick={() => this.props.showModal(booking)}
                        >
                          Edit
                        </Button>
                      </td>
                      <td>
                        <Button
                          variant="danger"
                          onClick={() => this.props.showModal2(booking)}
                        >
                          Delete
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default ListOfReservations;
