import React from "react";
import "../App.css";
import Container from "react-bootstrap/Container";
import { Row, Col, Form, Button, Card, Table, Modal } from "react-bootstrap";
import axios from "axios";
import NavBar from "./NavBar";
import AddRoomForm from "./AddRoomForm";

class RoomsComponent extends React.Component {
  constructor(props) {
    super(props);
    this.showModal = this.showModal.bind(this);
    this.hideModal = this.hideModal.bind(this);
    this.state = {
      show: false,
      room: {
        roomId: "",
        roomNumber: "",
        type: "",
        price: "",
        bedType: "",
        numberOfBeds: "",
        maxNumberOfGuests: "",
        smoking: "",
        description: "",
        isAvailable: "",
      },
      roomToEdit: {
        roomId: "",
        roomNumber: "",
        type: "",
        price: "",
        bedType: "",
        numberOfBeds: "",
        maxNumberOfGuests: "",
        smoking: "",
        description: "",
        isAvailable: "",
      },
      rooms: [],
    };
  }

  changeHandler = (event) => {
    this.setState({
      room: {
        ...this.state.room,
        [event.target.name]: event.target.value,
      },
    });
  };
  changeHandler2 = (event) => {
    this.setState({
      roomToEdit: {
        ...this.state.roomToEdit,
        [event.target.name]: event.target.value,
      },
    });
  };

  showModal = (room) => {
    console.log(room);
    this.setState({ show: true, roomToEdit: room });
  };

  hideModal = () => {
    this.setState({ show: false });
  };

  submitHandler = (event) => {
    event.preventDefault();
    const { roomToEdit } = this.state;
    const roomDetails = {
      roomId: roomToEdit.roomId,
      roomNumber: roomToEdit.roomNumber,
      type: roomToEdit.type,
      price: roomToEdit.price,
      bedType: roomToEdit.bedType,
