import React from "react";
import "../App.css";
import Container from "react-bootstrap/Container";
import { Row, Col, Form, Button, Card, Table, Modal } from "react-bootstrap";
import axios from "axios";
import NavBar from "./NavBar";

class RoomsComponent extends React.Component {
  constructor(props) {
    super(props);
    this.onOpenModal = this.onOpenModal.bind(this);
    this.onCloseModal = this.onCloseModal.bind(this);
    this.state = {
      open: true,
      roomNumber: "",
      type: "",
      price: "",
      bedType: "",
      numberOfBeds: "",
      maxNumberOfGuests: "",
      smoking: "",
      description: "",
      isAvailable: "",
      rooms: [],
    };
  }

  changeHandler = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };
  onOpenModal() {
    this.setState({ open: this.props.openModal });
  }

  onCloseModal() {
    this.setState({ open: false });
  }

  submitHandler = (event) => {
    event.preventDefault();
    const roomDetails = {
      roomNumber: this.state.roomNumber,
      type: this.state.type,
      price: this.state.price,
      bedType: this.state.bedType,
      numberOfBeds: this.state.numberOfBeds,
      maxNumberOfGuests: this.state.maxNumberOfGuests,
      smoking: this.state.smoking,
      description: this.state.description,
      available: this.state.available,
    };

    axios
      .post("http://localhost:8080/api/rooms/", roomDetails, {
