import React from "react";
import "../App.css";
import Container from "react-bootstrap/Container";
import {
  Row,
  Col,
  Form,
  Button,
  Card,
  Table,
  Modal,
  ModalBody,
} from "react-bootstrap";
import axios from "axios";
import NavBar from "./NavBar";
import AddRoomForm from "./AddRoomForm";
import RoomsList from "./RoomsList";

class RoomsComponent extends React.Component {
  constructor(props) {
    super(props);
    this.showModal2 = this.showModal2.bind(this);
    this.hideModal2 = this.hideModal2.bind(this);
    this.showModal = this.showModal.bind(this);
    this.hideModal = this.hideModal.bind(this);
    this.state = {
      show: false,
      show2: false,
      room: {
        roomId: "",
        roomNumber: "",
        type: "",
        price: "",
        bedType: "",
        numberOfBeds: "",
        maxNumberOfGuests: "",
        smoking: "",
        description: "",
        isAvailable: "",
      },
      roomToEdit: {
        roomId: "",
        roomNumber: "",
        type: "",
        price: "",
        bedType: "",
        numberOfBeds: "",
        maxNumberOfGuests: "",
        smoking: "",
        description: "",
        isAvailable: "",
      },
      rooms: [],
    };
  }

  changeHandler = (event) => {
    this.setState({
      room: {
        ...this.state.room,
        [event.target.name]: event.target.value,
      },
    });
  };
  changeHandler2 = (event) => {
    this.setState({
      roomToEdit: {
        ...this.state.roomToEdit,
        [event.target.name]: event.target.value,
      },
    });
  };

  showModal = (room) => {
    console.log(room);
    this.setState({ show: true, roomToEdit: room });
  };
  showModal2 = (room) => {
    console.log(room);
    this.setState({ show2: true, roomToEdit: room });
  };

  hideModal = () => {
    this.setState({ show: false });
  };
  hideModal2 = () => {
    this.setState({ show2: false });
  };

  submitHandler = (event) => {
    event.preventDefault();
    const { roomToEdit } = this.state;
    const roomDetails = {
      roomId: roomToEdit.roomId,
      roomNumber: roomToEdit.roomNumber,
      type: roomToEdit.type,
      price: roomToEdit.price,
      bedType: roomToEdit.bedType,
      numberOfBeds: roomToEdit.numberOfBeds,
      maxNumberOfGuests: roomToEdit.maxNumberOfGuests,
      smoking: roomToEdit.smoking,
      description: roomToEdit.description,
      available: roomToEdit.available,
    };

    axios
      .put(
        `http://localhost:8080/api/rooms/${roomToEdit.roomId}`,
        roomDetails,
        {
          // headers: {
          //   Authorization:
          //     "Bearer " + localStorage.getItem("access-token").replace(/"/g, ""),
          // },
        }
      )
      .then((res) => {
        console.log(res);
        window.location = "/rooms";
      })
      .catch((err) => console.log(err));

    axios
      .delete(`http://localhost:8080/api/rooms/${roomToEdit.roomId}`, {
        // headers: {
        //   Authorization:
        //     "Bearer " + localStorage.getItem("access-token").replace(/"/g, ""),
        // },
      })
      .then((res) => {
        console.log(res);
        window.location = "/rooms";
      })
      .catch((err) => console.log(err));
