import React from "react";
import "../App.css";
import Container from "react-bootstrap/Container";
import { Row, Col, Form, Button, Card, Table, Modal } from "react-bootstrap";
import axios from "axios";
import NavBar from "./NavBar";

class RoomsComponent extends React.Component {
  constructor(props) {
    super(props);
    this.showModal = this.showModal.bind(this);
    this.hideModal = this.hideModal.bind(this);
    this.state = {
      show: false,
      room: {
        roomNumber: "",
        type: "",
        price: "",
        bedType: "",
        numberOfBeds: "",
        maxNumberOfGuests: "",
        smoking: "",
        description: "",
        isAvailable: "",
      },
      roomToEdit: {
        roomNumber: "",
        type: "",
        price: "",
        bedType: "",
        numberOfBeds: "",
        maxNumberOfGuests: "",
        smoking: "",
        description: "",
        isAvailable: "",
      },
      rooms: [],
    };
  }

  changeHandler = (event) => {
    this.setState({
      room: {
        ...this.state.room,
        [event.target.name]: event.target.value,
      },
    });
  };

  showModal = (room) => {
    console.log(room);
    this.setState({ show: true, roomToEdit: room });
  };

  hideModal = () => {
    this.setState({ show: false });
  };

  submitHandler = (event) => {
    event.preventDefault();
    const { room } = this.state;
    const roomDetails = {
      roomNumber: room.roomNumber,
      type: room.type,
      price: room.price,
      bedType: room.bedType,
      numberOfBeds: room.numberOfBeds,
      maxNumberOfGuests: room.maxNumberOfGuests,
      smoking: room.smoking,
      description: room.description,
      available: room.available,
    };

    axios
      .post("http://localhost:8080/api/rooms/", roomDetails, {
        // headers: {
        //   Authorization:
        //     "Bearer " + localStorage.getItem("access-token").replace(/"/g, ""),
        // },
      })
      .then((res) => {
        console.log(res);
        //window.location = "/";
      })
      .catch((err) => console.log(err));
  };

  componentDidMount() {
    axios.get("http://localhost:8080/api/rooms/").then((res) => {
      this.setState({
        rooms: res.data.map((room) => {
          return {
            ...room,
          };
        }),
      }).catch((error) => console.log(error));
    });
  }
  render() {
    const { roomToEdit } = this.state;
    return (
      <div id="rooms-div">
        <NavBar />
        <br></br>

        {/* <Button variant="primary" onClick={this.showModal}>
          Launch demo modal
        </Button> */}

        <Modal size="lg" show={this.state.show} handleClose={this.hideModal}>
          <Modal.Header closeButton>
            <Modal.Title>
              {roomToEdit ? `Room:#${roomToEdit.roomNumber}` : "Room"}
              {console.log(roomToEdit)}
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form onSubmit={this.submitHandler}>
              <h3 style={{ textAlign: "center" }}>Edit Room details</h3>
              <Row className="mb-3">
                <Form.Group
                  as={Col}
                  className="mb-6"
                  controlId="formBasicEmail"
                >
                  <Form.Label>Room Number</Form.Label>
                  <Form.Control
                    type="text"
                    name="roomNumber"
                    value={roomToEdit.roomNumber}
                    onChange={this.changeHandler}
                  />
                </Form.Group>
                <Form.Group as={Col} controlId="formBasicEmail">
                  <Form.Label>Type</Form.Label>
                  <Form.Control
                    type="text"
                    name="type"
                    value={roomToEdit.type}
                    onChange={this.changeHandler}
                  />
                </Form.Group>
              </Row>
              <Row className="mb-6">
                <Form.Group
                  as={Col}
                  className="mb-6"
                  controlId="formBasicEmail"
                >
                  <Form.Label>Price</Form.Label>
                  <Form.Control
                    type="text"
                    name="price"
                    value={roomToEdit.price}
                    onChange={this.changeHandler}
                  />
                </Form.Group>
                <Form.Group as={Col} controlId="formBasicEmail">
                  <Form.Label>Bed Type</Form.Label>
                  <Form.Control
                    type="text"
                    name="bedType"
                    value={roomToEdit.bedType}
                    onChange={this.changeHandler}
                  />
                </Form.Group>
              </Row>
              <Row className="mb-6">
                <Form.Group
                  as={Col}
                  className="mb-3"
                  controlId="formBasicEmail"
                >
                  <Form.Label>Number Of Beds</Form.Label>
                  <Form.Control
                    type="text"
                    name="numberOfBeds"
                    value={roomToEdit.numberOfBeds}
                    onChange={this.changeHandler}
                  />
                </Form.Group>
                <Form.Group as={Col} controlId="formBasicEmail">
                  <Form.Label>Max Number Of Guests </Form.Label>
                  <Form.Control
                    type="text"
                    name="maxNumberOfGuests"
                    value={roomToEdit.maxNumberOfGuests}
                    onChange={this.changeHandler}
                  />
                </Form.Group>
              </Row>
              <Row className="mb-6">
                <Form.Group
                  as={Col}
                  className="mb-6"
                  controlId="formBasicEmail"
                >
                  <Form.Label>Smooking</Form.Label>
                  <Form.Select
                    as={Col}
                    aria-label="Smooking"
                    name="smoking"
                    value={roomToEdit.smoking}
                    onChange={this.changeHandler}
                  >
                    <option>Select</option>
                    <option
                      value={true}
                      name="smoking"
                      //   value={roomToEdit.smoking}
                      onChange={this.changeHandler}
                    >
                      Yes
                    </option>
                    <option
                      //   value={false}
                      name="smoking"
                      value={roomToEdit.smoking}
                      onChange={this.changeHandler}
                    >
                      No
                    </option>
                  </Form.Select>
                </Form.Group>

                <Form.Group as={Col} controlId="formBasicEmail">
                  <Form.Label>Description</Form.Label>
                  <Form.Control
                    type="text"
                    name="description"
                    value={roomToEdit.description}
                    onChange={this.changeHandler}
                  />
                </Form.Group>
              </Row>

              <Form.Group className="mb-6" controlId="formBasicPassword">
                <Form.Label>Is Available?</Form.Label>
                <Form.Select
                  as={Col}
                  aria-label="Smooking"
                  value={roomToEdit.isAvailable}
                  onChange={this.changeHandler}
                  name="isAvailable"
                >
                  <option>Select</option>

                  <option
                    value={true}
                    name="available"
                    onChange={this.changeHandler}
                  >
                    Yes
                  </option>
                  <option
                    value={false}
                    name="available"
                    onChange={this.changeHandler}
                  >
                    No
                  </option>
                </Form.Select>
              </Form.Group>
              <br></br>
              <Button id="button" type="submit">
                Submit
              </Button>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="secondary"
              show={this.state.show}
              onClick={this.hideModal}
            >
              Close
            </Button>
            <Button
              variant="primary"
              show={this.state.show}
              handleClose={this.hideModal}
            >
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>

        <Container fluid>
          <Row>
            <Col md={2}></Col>
            <Col md={5}>
              <Form id="room-form" onSubmit={this.submitHandler}>
                <h3 style={{ textAlign: "center" }}>Add Room details</h3>
                <Row className="mb-3">
                  <Form.Group
                    as={Col}
                    className="mb-3"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Room Number</Form.Label>
                    <Form.Control
                      type="text"
                      name="roomNumber"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                  <Form.Group as={Col} controlId="formBasicEmail">
                    <Form.Label>Type</Form.Label>
                    <Form.Control
                      type="text"
                      name="type"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                </Row>
                <Row className="mb-3">
                  <Form.Group
                    as={Col}
                    className="mb-3"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Price</Form.Label>
                    <Form.Control
                      type="text"
                      name="price"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                  <Form.Group as={Col} controlId="formBasicEmail">
                    <Form.Label>Bed Type</Form.Label>
                    <Form.Control
                      type="text"
                      name="bedType"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                </Row>
                <Row className="mb-3">
                  <Form.Group
                    as={Col}
                    className="mb-3"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Number Of Beds</Form.Label>
                    <Form.Control
                      type="text"
                      name="numberOfBeds"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                  <Form.Group as={Col} controlId="formBasicEmail">
                    <Form.Label>Max Number Of Guests </Form.Label>
                    <Form.Control
                      type="text"
                      name="maxNumberOfGuests"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                </Row>
                <Row className="mb-3">
                  <Form.Group
                    as={Col}
                    className="mb-3"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Smooking</Form.Label>
                    <Form.Select
                      as={Col}
                      aria-label="Smooking"
                      name="smoking"
                      onChange={this.changeHandler}
                    >
                      <option>Select</option>
                      <option
                        //    value={true}
                        name="smoking"
                        value={roomToEdit.roomNumber}
                        onChange={this.changeHandler}
                      >
                        Yes
                      </option>
                      <option
                        //   value={false}
                        value={roomToEdit.roomNumber}
                        name="smoking"
                        onChange={this.changeHandler}
                      >
                        No
                      </option>
                    </Form.Select>
                  </Form.Group>

                  <Form.Group as={Col} controlId="formBasicEmail">
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                      type="text"
                      name="description"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                </Row>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Label>Is Available?</Form.Label>
                  <Form.Select
                    as={Col}
                    aria-label="Smooking"
                    onChange={this.changeHandler}
                    name="isAvailable"
                    value={roomToEdit.isAvailable}
                  >
                    <option>Select</option>

                    <option
                      //    value={true}
                      value={roomToEdit.isAvailable}
                      name="available"
                      onChange={this.changeHandler}
                    >
                      Yes
                    </option>
                    <option
                      //    value={false}
                      value={roomToEdit.isAvailable}
                      name="available"
                      onChange={this.changeHandler}
                    >
                      No
                    </option>
                  </Form.Select>
                </Form.Group>

                <Button id="button" type="submit">
                  Submit
                </Button>
              </Form>
            </Col>

            <Col>
              <div id="image-div-room">
                <img src="/hotel.jpg" height="590" width="560"></img>
              </div>
            </Col>
          </Row>
        </Container>
        <br></br>
        <br></br>
        <Container>
          <Row>
            <Col>
              <h3 style={{ textAlign: "center", color: "white" }}>
                Available Rooms
              </h3>
              <Table striped bordered hover size="sm">
                <thead style={{ color: "white" }}>
                  <tr>
                    <th>Room Number</th>
                    <th>Room Type</th>
                    <th>Room Price</th>
                    <th>Bed Type</th>
                    <th colSpan={2} style={{ textAlign: "center" }}>
                      Action
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.rooms.map((room) => (
                    <tr key={room.roomId}>
                      <td>{room.roomNumber}</td>
                      <td>{room.type}</td>
                      <td>{room.price}</td>
                      <td>{room.bedType}</td>
                      <td>
                        <Button
                          class="btn btn-primary"
                          onClick={() => this.showModal(room)}
                        >
                          Edit
                        </Button>
                      </td>
                      <td>
                        <Button variant="danger">Delete</Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
export default RoomsComponent;
