import React from "react";
import { Row, Col, Form, Button, Card, Table, Modal } from "react-bootstrap";

class RoomsList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      rooms: [],
    };
  }
  componentDidMount() {
    axios.get("http://localhost:8080/api/rooms/").then((res) => {
      this.setState({
        rooms: res.data.map((room) => {
          return {
            ...room,
          };
        }),
      }).catch((error) => console.log(error));
    });
  }
  render() {
    <div>
      <h3 style={{ textAlign: "center", color: "white" }}>Available Rooms</h3>
      <Table striped bordered hover size="sm">
        <thead style={{ color: "white" }}>
          <tr>
            <th>Room Number</th>
            <th>Room Type</th>
            <th>Room Price</th>
            <th>Bed Type</th>
            <th colSpan={2} style={{ textAlign: "center" }}>
              Action
            </th>
          </tr>
        </thead>
        <tbody>
          {this.state.rooms.map((room) => (
            <tr key={room.roomId}>
              <td>{room.roomNumber}</td>
              <td>{room.type}</td>
              <td>{room.price}</td>
              <td>{room.bedType}</td>
              <td>
                <Button
                  class="btn btn-primary"
                  onClick={() => this.props.showModal(room)}
                >
                  Edit
                </Button>
              </td>
              <td>
                <Button variant="danger">Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>;
  }
}

export default RoomsList;
