import React from "react";
import "../App.css";
import Container from "react-bootstrap/Container";
import { Row, Col, Form, Button, Card, Table } from "react-bootstrap";
import axios from "axios";
import NavBar from "./NavBar";

class RoomsComponent extends React.Component {
  state = {
    roomNumber: "",
    type: "",
    price: "",
    bedType: "",
    numberOfBeds: "",
    maxNumberOfGuests: "",
    smoking: "",
    description: "",
    isAvailable: "",
    rooms: [],
  };

  changeHandler = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  submitHandler = (event) => {
    event.preventDefault();
    const roomDetails = {
      roomNumber: this.state.roomNumber,
      type: this.state.type,
      price: this.state.price,
      bedType: this.state.bedType,
      numberOfBeds: this.state.numberOfBeds,
      maxNumberOfGuests: this.state.maxNumberOfGuests,
      smoking: this.state.smoking,
      description: this.state.description,
      available: this.state.available,
    };

    axios
      .post("http://localhost:8080/api/rooms/", roomDetails, {
        // headers: {
        //   Authorization:
        //     "Bearer " + localStorage.getItem("access-token").replace(/"/g, ""),
        // },
      })
      .then((res) => {
        console.log(res);
        //window.location = "/";
      })
      .catch((err) => console.log(err));
  };
  //   fetchUsers = () => {
  //     axios
  //       .get("http://localhost:8080/api/rooms")
  //       .then((res) => {
  //           this.setState({
  //               rooms: res.data.map((room) => {
  //           return {
  //             ...room,
  //           };
  //         });
  //       })
  //       .catch((error) => console.log(error));
  //   }

  componentDidMount() {
    axios.get("http://localhost:8080/api/rooms/").then((res) => {
      this.setState({
        rooms: res.data.map((room) => {
          return {
            ...room,
          };
        }),
      }).catch((error) => console.log(error));
    });
  }
  render() {
    return (
      <div id="rooms-div">
        <NavBar />
        <br></br>
        <Container fluid>
          <Row>
            <Col md={2}></Col>
            <Col md={6}>
              <Form id="room-form" onSubmit={this.submitHandler}>
                <h3 style={{ textAlign: "center" }}>Add Room details</h3>
                <Row className="mb-3">
                  <Form.Group
                    as={Col}
                    className="mb-3"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Room Number</Form.Label>
                    <Form.Control
                      type="text"
                      name="roomNumber"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                  <Form.Group as={Col} controlId="formBasicEmail">
                    <Form.Label>Type</Form.Label>
                    <Form.Control
                      type="text"
                      name="type"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                </Row>
                <Row className="mb-3">
                  <Form.Group
                    as={Col}
                    className="mb-3"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Price</Form.Label>
                    <Form.Control
                      type="text"
                      name="price"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                  <Form.Group as={Col} controlId="formBasicEmail">
                    <Form.Label>Bed Type</Form.Label>
                    <Form.Control
                      type="text"
                      name="bedType"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                </Row>
                <Row className="mb-3">
                  <Form.Group
                    as={Col}
                    className="mb-3"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Number Of Beds</Form.Label>
                    <Form.Control
                      type="text"
                      name="numberOfBeds"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                  <Form.Group as={Col} controlId="formBasicEmail">
                    <Form.Label>Max Number Of Guests </Form.Label>
                    <Form.Control
                      type="text"
                      name="maxNumberOfGuests"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                </Row>
                <Row className="mb-3">
                  <Form.Group
                    as={Col}
                    className="mb-3"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Smooking</Form.Label>
                    <Form.Select
                      as={Col}
                      aria-label="Smooking"
                      name="smoking"
                      onChange={this.changeHandler}
                    >
                      <option>Select</option>
                      <option
                        value={true}
                        name="smoking"
                        onChange={this.changeHandler}
                      >
                        Yes
                      </option>
                      <option
                        value={false}
                        name="smoking"
                        onChange={this.changeHandler}
                      >
                        No
                      </option>
                    </Form.Select>
                  </Form.Group>

                  <Form.Group as={Col} controlId="formBasicEmail">
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                      type="text"
                      name="description"
                      onChange={this.changeHandler}
                    />
                  </Form.Group>
                </Row>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                  <Form.Label>Is Available?</Form.Label>
                  <Form.Select
                    as={Col}
                    aria-label="Smooking"
                    onChange={this.changeHandler}
                    name="isAvailable"
                  >
                    <option>Select</option>

                    <option
                      value={true}
                      name="available"
                      onChange={this.changeHandler}
                    >
                      Yes
                    </option>
                    <option
                      value={false}
                      name="available"
                      onChange={this.changeHandler}
                    >
                      No
                    </option>
                  </Form.Select>
                </Form.Group>

                <Button id="button" type="submit">
                  Submit
                </Button>
              </Form>
            </Col>

            <Col>
              <div id="image-div-room">
                <img src="/hotel.jpg" height="590" width="660"></img>
              </div>
            </Col>
            <Col md={2}></Col>
          </Row>
        </Container>
        <br></br>
        <br></br>
        <Container>
          <Row>
            <Col>
              <Table striped bordered hover size="sm">
                <thead>
                  <tr>
                    <th>Room Number</th>
                    <th>Room Type</th>
                    <th>Room Price</th>
                    <th>Bed Type</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.rooms.map((room) => (
                    <tr key={room.roomId}>
                      <td>{room.roomNumber}</td>
                      <td>{room.type}</td>
                      <td>{room.price}</td>
                      <td>{room.bedType}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
export default RoomsComponent;
